import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import "./App.css";
// import img1 from "./img/Amul Bread.png";
// import img2 from "./img/Amul Butter.jpeg";
// import img3 from "./img/Amul Cheese.jpeg";
// import img4 from "./img/Amul Combo.png";
// import img5 from "./img/Amul Milk .jpeg";
// import img6 from "./img/Kissan Jam.jpeg";
import ProductCard from "./ProductCard";



function Homee(){

  const [products, setproducts] = useState([]);

  const fetchData = () => {
    return fetch("database?action=getAllProducts")
          .then((response) => response.json())
          .then((data) => setproducts(data['data']));
  }

  useEffect(() => {
    fetchData();
  },[])

  return (
    <div className="homediv d-flex flex-row align-items-stretch">
      <div className="row">

        {
          products.map((pr)=>(
            <ProductCard name={pr.ProductName} price={pr.ProductPrice} img={pr.ProductImage}/>
          ))
        }


      </div>
    </div>
    );
}

export default Homee;
