import React, {useState} from "react";
import { TiShoppingCart } from "react-icons/ti";


const Login = () =>{
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState([]);

  const handleLogin = (event) =>{
    event.preventDefault();
    console.log(email)
    console.log(password)
    useEffect(() => {
      fetch('https://script.google.com/macros/s/AKfycbwesrsULT_ERfAPmqsTAaX34zWTqhdMRHa0ytCsJzdlF8lyvR7gVOGInOtmcTLa48U/exec?action=validateUser')
         .then((res) => res.json())
         .then((data) => {
            console.log(data);
         })
         .catch((err) => {
            console.log(err.message);
         });
   }, []);
  }

  return (
    <form onSubmit={handleLogin}>
    <div className="App-header">
      <div>
        <TiShoppingCart className="navbarlogin"></TiShoppingCart>
      </div>
      <div>
        <input
          type="text"
          placeholder="Email Id"
          required
          className="inputlogin"
          name="email"
          onChange={event => setEmail(event.target.value)}
          value={email}
        />
      </div>
      <div>
        <input
          type="password"
          placeholder="password"
          required
          className="inputlogin" 
          name="password"
          onChange={event => setPassword(event.target.value)}
          value={password}
        />
      </div>
      <div>
          <button className="buttonforlogin" type="submit">Login</button>
      </div>
    </div>
    </form>
  );

}



export default Login;
