import React, { useEffect, useState } from "react";
import { BsCart2 } from "react-icons/bs";

import CartCard from "./CartCard";
import ProductCard from "./ProductCard";


function Cart(){

  const userId = localStorage.getItem('userId')
  const [products, setproducts] = useState([]);

  const fetchData = () => {
    return fetch('database?action=getCartDetails', {
         redirect: "follow",
         method: 'POST',
         body: JSON.stringify({
            cust_id: userId,
         }),

      })
         .then((res) => res.json())
         .then((data) => {
            // setproducts(data['ItemDetails'])
            console.log(data)
         })
         .catch((err) => {
            console.log(err);
         });
  }

  useEffect(() => {
    fetchData();
  },[])  


  return (
    <div className="bgforcart">
      <div className="cart">
        <div className="cartlogologo">
          <BsCart2 className="cartcart"></BsCart2>
        </div>

        {
          products.map((pr)=>(
            <CartCard name={pr.ProductName} price={pr.ProductPrice} img={pr.ProductImage}/>
          ))
        }
        <CartCard />



        <br />

        <div>
          <h3>Recommended products</h3>
        </div>
        <br />

        <div className="d-flex flex-row align-items-stretch">
          <div className="row">
          <ProductCard name="pr1"/>
          </div>
        </div>

        <br />
        <br />
      </div>
    </div>
  );
}

export default Cart;
